import { useState } from 'react';
import { useEffect } from 'react';
import './game.css'
const Game = () => {
    const[snake,setsnake]=useState([{x:90,y:30},{x:60,y:30},{x:30,y:30}])
    const[direction,setdirection]=useState('R');
    const[updateinterval,setupdateinterval]=useState(1000);
    
    setInterval(() => {
        let newsnake=[...snake];
        newsnake.pop();
        switch (direction) {
                case 'R':
                newsnake.unshift({x:snake[0].x+30,y:snake[0].y})
                break;
                case 'L':
                newsnake.unshift({x:snake.x[0]-30,y:snake[0].y})
                break;
                case 'U':
                newsnake.unshift({x:snake[0].x,y:snake[0].y+30}) 
                break;
                case 'D':
                newsnake.unshift({x:snake[0].x,y:snake[0].y-30})
                break;
                default:
                break;
        }
        setsnake(snake=>newsnake); 
        console.log('update')       
    }, updateinterval);


    return (  
        <div id="gamepage">
            <div id="box">
             {snake.map((element)=>{
               return <div className="snakeelement" style={{left:`${element.x}px`,top:`${element.y}px`}}></div>
             })}                
            </div>
        </div>
    );
}
 
export default Game;
