import { useState, useEffect } from 'react';
import './game.css';

const Game = () => {
    const [snake, setSnake] = useState([{ x: 90, y: 30 }, { x: 60, y: 30 }, { x: 30, y: 30 }]);
    const [direction, setDirection] = useState('R');
    const [updateInterval, setUpdateInterval] = useState(1000);
    const[TX,setTX]=useState(Math.random()*500);
    const[TY,setTY]=useState(Math.random()*500);

    // ✅ Snake movement logic inside useEffect with proper cleanup
    useEffect(() => {
        const interval = setInterval(() => {
            if(Math.abs(TX-snake[0].x)<30&&Math.abs(TY-snake[0].y)<30){
                setTX(Math.random()*500);
                setTY(Math.random()*500);
            }
            console.log(Math.abs(TX-snake[0].x))
            setSnake((prevSnake) => {
                let newsnake = [...prevSnake];
                newsnake.pop();

                switch (direction) {
                    case 'R':
                        newsnake.unshift({ x: prevSnake[0].x + 30, y: prevSnake[0].y });
                        break;
                    case 'L':
                        newsnake.unshift({ x: prevSnake[0].x - 30, y: prevSnake[0].y });
                        break;
                    case 'U':
                        newsnake.unshift({ x: prevSnake[0].x, y: prevSnake[0].y - 30 });
                        break;
                    case 'D':
                        newsnake.unshift({ x: prevSnake[0].x, y: prevSnake[0].y + 30 });
                        break;
                    default:
                        break;
                }

                return newsnake;
            });
        }, updateInterval);

        return () => clearInterval(interval);  // ✅ Cleanup interval
    }, [direction, updateInterval]);

    // ✅ Correct Key Event Listener Logic
    useEffect(() => {
        const handleKeyDown = (e) => {
            switch (e.code) {
                case 'ArrowUp':
                    setDirection('U');
                    break;
                case 'ArrowDown':
                    setDirection('D');
                    break;
                case 'ArrowLeft':
                    setDirection('L');
                    break;
                case 'ArrowRight':
                    setDirection('R');
                    break;
                default:
                    break;
            }
        };

        window.addEventListener('keydown', handleKeyDown);

        return () => {
            window.removeEventListener('keydown', handleKeyDown);  // ✅ Proper cleanup
        };
    }, []);  // ✅ Only runs once on mount

    return (
        <div id="gamepage">
            <div id="box">
                {snake.map((element, index) => (
                    <div
                        key={index}
                        className="snakeelement"
                        style={{ left: `${element.x}px`, top: `${element.y}px` }}
                    ></div>
                ))}
                <div id="target" style={{left:`${TX}px`,top:`${TY}px`}} ></div>
            </div>
        </div>
    );
};

export default Game;
